    document.body.classList.add('fonts-loaded');
}, function () {
    console.log('Fonts not available');
});
store.subscribe(storeState);
sugarDateTime();
m.route(document.body, "/", {
    "/login": {
        onmatch: function () {
            if (loggedIn()) {
                m.route.set("/");
            }
