        render: function () {
            return m(App, m(Login));
        }
    },
    "/": {
        onmatch: function () {
            if (!loggedIn()) {
                m.route.set("/login");
            }
        },
        render: function () {
            return m(App, m(TodoPage, { context: TodoContext.Normal }));
        }
    },
