/* CSS resets */

/* http://meyerweb.com/eric/tools/css/reset/
   v2.0 | 20110126
   License: none (public domain)
*/

html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed, 
figure, figcaption, footer, header, hgroup, 
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
	margin: 0;
	padding: 0;
	border: 0;
	font: inherit;
	font-size: 100%;
	vertical-align: baseline;
}
/* HTML5 display-role reset for older browsers */
article, aside, details, figcaption, figure, 
footer, header, hgroup, menu, nav, section {
	display: block;
}
body {
	line-height: 1;
}
ol, ul {
	list-style: none;
}
blockquote, q {
	quotes: none;
}
blockquote:before, blockquote:after,
q:before, q:after {
	content: '';
	content: none;
}
table {
	border-collapse: collapse;
	border-spacing: 0;
}

/* Forms reset https://gist.github.com/anthonyshort/552543 */

input,
label,
select,
button,
textarea
{
	margin:0;
	border:0;
	padding:0;
	display:inline-block;
	vertical-align:middle;
	white-space:normal;
	background:none;
	line-height:1;

	/* Browsers have different default form fonts */
	font-size:15px;
  font-family: inherit;
}

textarea {
    white-space: pre-wrap;
}

/* Remove the stupid outer glow in Webkit */
input:focus,
textarea:focus
{
	outline:0;
}
button
{
	  -webkit-tap-highlight-color: transparent;
}

/* Box Sizing Reset
-----------------------------------------------*/

/* Text Inputs
-----------------------------------------------*/

input[type=date],
input[type=datetime],
input[type=datetime-local],
input[type=email],
input[type=month],
input[type=number],
input[type=password],
input[type=range],
input[type=search],
input[type=tel],
input[type=text],
input[type=time],
input[type=url],
input[type=week]
{
}

/* Button Controls
-----------------------------------------------*/

input[type=checkbox],
input[type=radio]
{
    min-width: 13px;
    min-height: 13px;
	  width:13px;
	  height:13px;
}

/* File Uploads
-----------------------------------------------*/

input[type=file]
{

}

/* Search Input
-----------------------------------------------*/

/* Make webkit render the search input like a normal text field */
input[type=search]
{
	-webkit-appearance:textfield;
	-webkit-box-sizing:content-box;
}

/* Turn off the recent search for webkit. It adds about 15px padding on the left */
::-webkit-search-decoration
{
	display:none;
}

/* Buttons
-----------------------------------------------*/

button,
input[type="reset"],
input[type="button"],
input[type="submit"]
{
	/* Fix IE7 display bug */
	overflow:visible;
	width:auto;
}

/* IE8 and FF freak out if this rule is within another selector */
::-webkit-file-upload-button
{
	padding:0;
	border:0;
	background:none;
}

/* Textarea
-----------------------------------------------*/

textarea
{
	/* Move the label to the top */
	vertical-align:top;
	/* Turn off scroll bars in IE unless needed */
	overflow:auto;
}

/* Selects
-----------------------------------------------*/

select
{

}

select[multiple] 
{
	/* Move the label to the top */
	vertical-align:top;
}

*,
*:before,
*:after {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

button::-moz-focus-inner,
button::-moz-focus-inner {
    border: 0;
}

/* Variables */
/* https://material.io/tools/color/#!/?view.left=0&view.right=1&primary.color=9ed36a&secondary.color=8341c6&primary.text.color=332b2f */

:root {
    --primary: #9ed36a;
    --primary-90: #9ed36a90;
    --primary-light: #d1ff9a;
    --primary-dark: #6da13c;
    --primary-dark-90: #6da13c90;
    --secondary: #8341c6;
    --secondary-light: #b670fa;
    --secondary-lighter: #c7aae5;
    --secondary-lightest: #e1d1f1;
    --secondary-medium: #7230b5;
    --secondary-darkish: #611fa5;
    --secondary-dark: #500e94;
    --secondary-dark-90: #500e9490;
    --secondary-darker: #371955;
    --on-primary: #332b2f;
    --on-secondary: #ffffff;
    --background-light: #fafafa;
    --background: #eeeeee;
    --background-medium: #bbb;
    --background-dark: #888888;
    --background-darkish: #6b6b6b;
    --background-darker: #555555;
    --shadow: #0007;
    --shadow-dark: #000a;
    --shadow-faint: #0003;
    --outline: #aaa;
    --outline-less: #bbb;
    --outline-dark: #777;
    --outline-faint: #0003;
    --outline-focus-light: #fff8;
    --outline-focus-dark: #000;

    --warning: #f4c242;

    --upcoming: #76dce4;
    --upcoming-90: #76dce490;
    --upcoming-dark: #22a7b0;
    --upcoming-dark-90: #22a7b090;
    --completed: #eabc5a;
    --completed-90: #eabc5a90;
    --completed-dark: #b28a27;
    --completed-dark-90: #b28a2790;
}

/* Body */

body {
    background-color: var(--background);
}

/* Login and edit forms */

.modal-container {
    background-color: var(--primary-light);
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    height: 100vh;
}

.modal-form {
    box-shadow: 0px 1px 5px 0px var(--shadow);
    padding: 0.8rem 1.5rem;
    background-color: var(--secondary);
    width: 100%;
}

.login-form {
    border: 0.5px solid var(--outline-faint);
    border-radius: 0.5rem;
    width: 90%;
    max-width: 23rem;
    padding-bottom: 1.4rem;
}

.edit-form {
    padding: 0.8rem 0.6rem;
}

.text-input {
    display: block;
    margin: 1rem 0;
    font-size: 1.5rem;
    width: 100%;
    border: 1px solid var(--outline);
    padding: 0.2em;
    border-radius: 0.2em;
    color: var(--on-primary);
    background-color: var(--secondary-lighter);
    box-shadow: 0px 1px 2px 0px var(--shadow-faint) inset;
}

.text-input:focus {
    background-color: var(--secondary-lightest);
}

.login-signup {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
}

#title {
    max-height: 2em;
    transition: max-height 0.3s, font-size 0.3s;
    resize: none;
}

#title.expand {
    font-size: 1.2rem;
    max-height: 7em;
}

.item-deadline.on-edit-form {
    background: var(--background);
    margin: 0;
    width: 100%;
    margin-top: 1rem;
    margin-bottom: 0.7rem;
    padding: 0 0.7rem;
    border-radius: 0.3rem;
    height: 2.15rem;
    line-height: 2.15rem;
    font-size: 1.3rem;
}

.show-in-deadlines {
    display: flex;
    align-items: center;
    margin: 0.6rem 0;
    padding: 0.5rem 0.3rem;
}

.deadline-check-label {
    color: var(--on-secondary);
    font-size: 1.3rem;
}

.pill-button {
    border-radius: 999rem;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0.3em 0.8em;
    transition: background-color 0.1s;
    outline: none;
    cursor: pointer;
}


.pill-button.large {
    padding: 0.8rem 2rem;
}

.pill-button.large-fixed {
    padding: 0.8rem 0.8rem;
    min-width: 12rem;
}

.pill-button.on-secondary {
    color: var(--on-secondary);
}

.pill-button.on-primary {
    color: var(--on-primary);
}

.pill-button.on-secondary.fill {
    background-color: var(--secondary-medium);
}

.pill-button:focus.on-secondary,
.pill-button:hover.on-secondary {
    background-color: var(--secondary-dark-90);
}

.pill-button:focus.on-primary,
.pill-button:hover.on-primary {
    background-color: var(--primary-dark-90);
}

.pill-button:active.on-secondary {
    background-color: var(--secondary-dark);
}

.pill-button:active.on-primary {
    background-color: var(--primary-dark);
}

.pill-button.fill.processing,
.pill-button:focus.fill.processing {
    opacity: 0.8;
    background-color: var(--shadow-faint);
}

.text-button {
    color: var(--on-secondary);
    text-decoration: underline;
    padding: 0.1rem;
    border: 1px solid transparent;
    cursor: pointer;
    outline: none;
}

.text-button.on-background {
    color: var(--on-primary);
}

.text-button:focus {
    border: 1.3px dashed var(--outline-focus-light);
}

.text-button.on-background:focus {
    border: 1.3px dashed var(--secondary);
}

.text-button:active {
    border: 1.3px solid var(--outline-focus-light);
}

.text-button.on-background:active {
    border: 1.3px solid var(--secondary);
}

.button-text {
    font-size: 1.6rem;
}

/* Header */

.header {
    position: relative;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0.1rem 0.6rem;
    padding-left: 0.1rem;
    height: 3.1rem;
    /*box-shadow: 0 1px 3px 0 var(--shadow-faint);*/
    transition: background-color 0.5s;
    z-index: 1000;
}

.header.normal {
    background: var(--primary);
    border-bottom: 1.5px solid var(--primary-dark);
}

.header.upcoming {
    background: var(--upcoming);
    border-bottom: 1.5px solid var(--upcoming-dark);
}

.header.completed {
    background: var(--completed);
    border-bottom: 1.5px solid var(--completed-dark);
}

.header-first {
    display: flex;
    align-items: center;
}

.menu-icon {
    width: 2.8rem;
    height: 2.8rem;
    margin-right: 0.8rem;
    border-radius: 50%;
    padding: 0.4rem;
    background-color: transparent;
    transition: background-color 0.1s;
    cursor: pointer;
    outline: none;
}

.normal .menu-icon:hover,
.normal .menu-icon:focus {
    background-color: var(--primary-dark-90);
}
.normal .menu-icon:active {
    background-color: var(--primary-dark);
}
.upcoming .menu-icon:hover,
.upcoming .menu-icon:focus {
    background-color: var(--upcoming-dark-90);
}
.upcoming .menu-icon:active {
    background-color: var(--upcoming-dark);
}
.completed .menu-icon:hover,
.completed .menu-icon:focus {
    background-color: var(--completed-dark-90);
}
.completed .menu-icon:active {
    background-color: var(--completed-dark);
}

.header-last {
    display: flex;
    align-items: center;
}

.cloud {
    margin-right: 0.8rem;
    width: 1.8rem;
    height: 1.8rem;
    opacity: 1;
    transition: opacity 2s ease-in;
    will-change: opacity;
}

.cloud-hidden {
    opacity: 0;
}

.cloud-path {
    transition: fill 0.5s;
}
.normal .cloud-path {
    fill: var(--primary-dark);
}
.upcoming .cloud-path {
    fill: var(--upcoming-dark);
}
.completed .cloud-path {
    fill: var(--completed-dark);
}

.logout-button {
    border: 1.5px solid black;
    border-radius: 999rem;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0.5rem 1rem;
    margin-top: 1rem;
}

.logout-button:active {
    background-color: #eee;
}

.noscript-header {
    background-color: var(--warning);
    font-size: 1.4rem;
    font-family: sans-serif;
    padding: 1.2rem 0;
    text-align: center;
}

/* Message */

.message {
    position: fixed;
    bottom: 0;
    text-align: center;
    font-size: 1.2rem;
    background-color: var(--warning);
    width: 100%;
    padding: 1rem;
    opacity: 1;
    transition: opacity 0.3s;
    min-height: 3.2rem;
    z-index: 40000;
}

.message.hidden {
    opacity: 0;
    pointer-events: none;
}

/* Menu */

.menu-container {
    height: 100%;
    min-height: calc(100vh - 3.1rem);
}

.menu-shadow {
    position: absolute;
    left: 0;
    width: 100vw;
    height: 100%;
    z-index: 99999;
    background-color: black;
    opacity: 0;
    pointer-events: none;
    transition: 0.1s opacity ease-out;
    will-change: opacity;
}

.menu-show .menu-shadow {
    pointer-events: auto;
    opacity: 0.2;
}

.menu-spacer {
    display: none;
}

.menu {
    color: var(--on-primary);
    background-color: var(--background-light);
    width: 80vw;
    height: 100%;
    z-index: 100000;
    position: absolute;
    top: 0;
    left: -80vw;
    opacity: 0;
    pointer-events: none;
    transition: left 0.25s ease-in-out, opacity 0.01s linear 0.25s;
    will-change: left, opacity;
}

.menu-show .menu {
    left: 0;
    opacity: 1;
    pointer-events: auto;
    transition: left 0.25s ease-out, opacity 0.01s linear;
}
@keyframes slide-in {
    from {
        left: -80vw;
    }
    to {
        left: 0;
    }
}

.menu-list {
    padding: 1rem 0;
    border-top: 1px solid var(--outline-faint);
    border-bottom: 1px solid var(--outline-faint);
    font-size: 1.3rem;
}

.main-nav-item {
    display: block;
    padding: 1rem;
    padding-left: 2rem;
    color: var(--on-primary);
    text-decoration: none;
    transition: background-color 0.1s;
    outline: none;
	  -webkit-tap-highlight-color: transparent;
}

.main-nav-item:focus,
.main-nav-item:hover {
    background-color: var(--background);
}

.main-nav-item:focus.selected,
.main-nav-item.selected {
    color: var(--secondary-dark);
    background-color: var(--secondary-lightest);
}

.main-nav-item.selected:focus,
.main-nav-item.selected:hover {
    background-color: var(--secondary-lighter);
}

.main-nav-item:active {
    background-color: var(--secondary-light);
}

.username {
    font-weight: bold;
}

.menu-username {
    font-size: 1rem;
    padding: 1rem;
    padding-left: 2rem;
    padding-bottom: 0.7rem;
    text-decoration: none;
    transition: background-color 0.1s;
    outline: none;
}

.menu-logout {
    padding-left: 1.9rem;
}

/* Main styles */

.page-container {
    background: var(--background);
    display: flex;
    flex-direction: row;
    align-items: flex-start;
    position: relative;
    overflow-x: hidden;
}

.todo-container {
    display: flex;
    flex-direction: column;
    padding-bottom: 6.5rem;
    min-width: 100vw;
}

.todo-section {
    flex-basis: 100%;
    overflow: hidden;
}

.section-enter {
    animation: section-enter 0.5s;
}
@keyframes section-enter {
    from {
        max-height: 0vh;
        opacity: 0;
    }
    to {
        max-height: 100vh;
        opacity: 1;
    }
}

.section-exit {
    animation: section-exit 0.5s linear;
}
@keyframes section-exit {
    from {
        max-height: 100vh;
        opacity: 1;
    }
    to {
        max-height: 0vh;
        opacity: 0;
    }
}

.section-title {
    font-size: 1.15rem;
    padding: 1rem 0 0.4rem 0.8rem;
    background: var(--background);
    text-transform: capitalize;
}

.normal .section-title {
    color: var(--primary-dark);
}
.upcoming .section-title {
    color: var(--upcoming-dark);
}
.completed .section-title {
    color: var(--completed-dark);
}

.section-before {
    width: 100%;
    border-top: 0.5px solid var(--secondary);
}

.section-after {
    width: 100%;
    border-top: 0.5px solid var(--outline-faint);
    border-bottom: 0.5px solid var(--secondary);
    margin-top: 1px;
}

.todo-list {
    position: relative;
    z-index: 1000;
    overflow-x: hidden;
}

.normal .todo-list {
    background-color: var(--primary-90);
}
.upcoming .todo-list {
    background-color: var(--upcoming-90);
}
.completed .todo-list {
    background-color: var(--completed-90);
}

.item {
    display: flex;
    flex-direction: column;
    border-top: 0.5px solid var(--outline-faint);
    background-color: var(--background-light);
    color: var(--on-primary);
    font-size: 1.1rem;
}

.item-enter {
    animation: item-enter 0.3s ease-in;
}
@keyframes item-enter {
    from {
        opacity: 0;
        max-height: 0;
        overflow-y: hidden;
        z-index: -1;
    }
    to {
        opacity: 1;
        max-height: 6rem;
    }
}

.item-exit {
    animation: item-exit 0.5s;
    overflow: hidden;
    position: relative;
    border: 0px solid transparent;
}
@keyframes item-exit {
    from {
        left: 0;
        max-height: 6rem;
    }
    to {
        left: 100%;
        max-height: 0;
    }
}

.item-main::before {
    content: '';
    width: 100%;
    height: 100%;
    position: absolute;
    left: 0;
    top: 0;
    background: linear-gradient(transparent 3.95em, white 5.2em);
    transition: opacity 0.3s;
    pointer-events: none;
}
.selected .item-main::before {
    opacity: 0;
}

.item-main {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding-top: 0.7rem;
    padding-bottom: 0.7rem;
    padding-left: 0.7rem;
    padding-right: 0.4rem;
    position: relative;
}

.item-first {
    display: flex;
    justify-content: flex-start;
    align-items: flex-start;
}

.item-title {
    line-height: 1.2em;
    white-space: pre-wrap;
    margin-left: 0.2rem;
    font-weight: bold;
    overflow: hidden;
    max-height: 4.55em;
    transition: max-height 0.3s;
    word-wrap: anywhere;
}

.selected .item-title {
    max-height: 20em;
    overflow-y: auto;
    animation: enlarge 0.3s ease-out;
}
@keyframes enlarge {
    0% {
        max-height: 3.75em;
        overflow-y: hidden;
    }
    99% {
        max-height: 20em;
    }
    100% {
        max-height: 20em;
        overflow-y: auto;
    }
}


.item-deadline {
    font-style: italic;
    font-size: 0.9rem;
    color: var(--secondary-dark);
    line-height: 1.2em;
    margin-left: 0.5rem;
    text-align: right;
    min-width: 5rem;
    max-height: 4.55em;
    padding-right: 0.4rem;/* stop weird clipping */
    overflow-y: hidden;
    overflow-x: hidden;/*seems to be necessary to avoid scrollbar*/
}

.selected .item-deadline {
    max-height: 20rem;
    overflow-y: auto;
}

input[type=checkbox] {
    min-width: 0.5em;
    width: 0.5em;
    margin: 0;
    padding: 0;
    opacity: 0;
    align-self: center;
}

.css-check {
    display: inline-block;
    width: 1.2em;
    height: 1.2em;
    margin-left: -0.5em;
    margin-right: 0.6em;
    vertical-align: bottom;
    border: 1.7px solid var(--outline);
    border-radius: 0.2rem;
    align-self: center;
}

.css-check.on-secondary {
    background: var(--on-secondary);
}

input[type=checkbox] + .css-check::after {
    font-size: 1.2rem;
    content: '✓';
    text-align: center;
    position: relative;
    bottom: 0.1em;
    opacity: 0;
    color: var(--primary-dark);
    transition: opacity 0.07s ease-out;
}

input[type=checkbox]:focus + .css-check {
    box-shadow: 0 0 3px 0 var(--shadow);
}
input[type=checkbox]:focus + .css-check.on-secondary {
    box-shadow: none;
    background-color: var(--background-medium);
}

input[type=checkbox]:checked + .css-check {
    border: 1.7px solid var(--primary-dark);
}

input[type=checkbox]:checked + .css-check::after {
    opacity: 1;
}

.item:hover .item-options {
    will-change: height, padding;
}

.item-options {
    background-color: var(--secondary-light);
    display: flex;
    justify-content: flex-start;
    align-items: center;
    box-shadow: 0 0 2px 0 var(--shadow) inset;
    height: 0;
    overflow: hidden;
    transition: height 0.3s, padding 0.3s;
    padding: 0 0.3rem;
}

.selected .item-options {
    height: 2.5rem;
    padding: 0.3rem 0.3rem;
}

.option-button {
    padding: 0.4em 0.8em;
    margin-top: 0.6em;
    margin-bottom: 0.6em;
}

/* FAB */

.fab {
    color: var(--on-secondary);
    background-color: var(--secondary);
    box-shadow: 0px 1px 5px 0px var(--shadow-dark);
    font-size: 2.5rem;
    border-radius: 50%;
    width: 3.5rem;
    height: 3.5rem;
    position: fixed;
    right: 1.5rem;
    bottom: 1.5rem;
    z-index: 10000;
    transition: background-color 0.1s;
    cursor: pointer;
}

.fab:focus {
    border: 1.5px solid var(--outline-focus-light);
    outline: none;
}

.fab:hover {
    background-color: var(--secondary-darkish);
}

.fab:active {
    border: 1.5px solid var(--outline-faint);
    background-color: var(--secondary-dark);
}

.undo {
    background-color: var(--background-dark);
    box-shadow: 0px 1px 5px 0px var(--shadow-dark);
    border-radius: 0.5rem;
    position: fixed;
    padding: 0.1rem;
    left: 1.5rem;
    bottom: 1.5rem;
    z-index: 10000;
    transition: background-color 0.1s, opacity 0.2s;
}

.dismiss-button,
.undo-button {
    font-size: 1.4rem;
    color: var(--on-secondary);
    margin: 0.1rem;
    padding: 0.5rem 0.3rem;
    border-radius: 0.3rem;
}

.dismiss-button:focus,
.undo-button:focus {
    outline: none;
    background-color: var(--background-darkish);
}

.dismiss-button:active,
.undo-button:active {
    background-color: var(--background-darker);
}

.undo-hidden {
    opacity: 0;
    pointer-events: none;
}

/* Desktop layout */

@media only screen and (min-width: 700px) {
    .menu-shadow {
        display: none;
    }

    .menu-spacer {
        display: block;
        width: 0;
        height: 100%;
        transition: width 0.2s ease-out;
        will-change: width;
    }

    .menu-show .menu-spacer {
        width: 14rem;
        margin-right: 1rem;
    }

    .menu {
        width: 14rem;/* if change this, remember to change touch gesture code */
        left: -14rem;
        transition: left 0.2s ease-out, opacity 0.01s linear 0.2s;
        will-change: left, opacity;
        z-index: auto;
    }

    .menu-show .menu {
        transition: left 0.2s ease-out, opacity 0.01s linear;
    }

    .todo-container {
        flex-direction: row;
        width: 100vw;
        min-width: auto;
    }

    .todo-section {
        padding: 0 1rem;
    }

    .section-enter {
        animation: section-enter 0.5s;
    }
    @keyframes section-enter {
        from {
            max-width: 0vw;
            opacity: 0;
        }
        to {
            max-width: 100vw;
            opacity: 1;
        }
    }

    .section-exit {
        animation: section-exit 0.5s linear;
    }
    @keyframes section-exit {
        from {
            max-width: 100vw;
            opacity: 1;
        }
        to {
            max-width: 0vw;
            opacity: 0;
        }
    }


    .edit-form {
        border: 0.5px solid var(--outline-faint);
        border-radius: 0.5rem;
        max-width: 35rem;
        padding: 0.8rem 1rem;
    }
}
